mapper.select:
select 

from os_meeting_training omt
where omt.TRAINING_ID = #{trainingId,jdbcType=INTEGER}

mapper.update:
update os_meeting_training omt

  
    TRAINING_ID = #{record.trainingId,jdbcType=INTEGER},
  
  
    NAME = #{record.name,jdbcType=VARCHAR},
  
  
    TRAINING_TYPE = #{record.trainingType,jdbcType=VARCHAR},
  
  
    ADAPT_OBJECT = #{record.adaptObject,jdbcType=VARCHAR},
  
  
    REGISTRATION_TIME = #{record.registrationTime,jdbcType=TIMESTAMP},
  
  
    REGISTRATION_DEADLINE = #{record.registrationDeadline,jdbcType=TIMESTAMP},
  
  
    TRAINING_START_TIME = #{record.trainingStartTime,jdbcType=TIMESTAMP},
  
  
    TRAINING_END_TIME = #{record.trainingEndTime,jdbcType=TIMESTAMP},
  
  
    ADDRESS = #{record.address,jdbcType=VARCHAR},
  
  
    REGISTRATION_WAY = #{record.registrationWay,jdbcType=VARCHAR},
  
  
    MAX_PLAYERS = #{record.maxPlayers,jdbcType=INTEGER},
  
  
    TRAINING_WAY = #{record.trainingWay,jdbcType=VARCHAR},
  
  
    PRICE = #{record.price,jdbcType=INTEGER},
  
  
    FILE_ID = #{record.fileId,jdbcType=VARCHAR},
  
  
    FILE_NAME = #{record.fileName,jdbcType=VARCHAR},
  
  
    PREVIEW_FILE_ID = #{record.previewFileId,jdbcType=VARCHAR},
  
  
    PREVIEW_FILE_NAME = #{record.previewFileName,jdbcType=VARCHAR},
  
  
    BRIEF = #{record.brief,jdbcType=VARCHAR},
  
  
    BRIEF_FILE_ID = #{record.briefFileId,jdbcType=VARCHAR},
  
  
    BRIEF_FILE_NAME = #{record.briefFileName,jdbcType=VARCHAR},
  
  
    ANNOUNCEMENTS = #{record.announcements,jdbcType=VARCHAR},
  
  
    ANNO_FILE_ID = #{record.annoFileId,jdbcType=VARCHAR},
  
  
    ANNO_FILE_NAME = #{record.annoFileName,jdbcType=VARCHAR},
  
  
    CONTACT = #{record.contact,jdbcType=VARCHAR},
  
  
    PHONE = #{record.phone,jdbcType=VARCHAR},
  
  
    MOBILE = #{record.mobile,jdbcType=VARCHAR},
  
  
    EMAIL = #{record.email,jdbcType=VARCHAR},
  
  
    SORT_INDEX = #{record.sortIndex,jdbcType=INTEGER},
  
  
    IS_VISIBLE = #{record.isVisible,jdbcType=BIT},
  
  
    CREATE_TIME = #{record.createTime,jdbcType=TIMESTAMP},
  
  
    UPDATE_TIME = #{record.updateTime,jdbcType=TIMESTAMP},
  


  
    and omt.TRAINING_ID = #{map.trainingId}
  
  
    and omt.TRAINING_ID in (#{i})
  
  
    and omt.NAME = #{map.name}
  
  
    and omt.NAME like '%' || #{map.like_name} || '%'
  
  
    and omt.NAME in (#{i})
  
  
    and omt.TRAINING_TYPE = #{map.trainingType}
  
  
    and omt.TRAINING_TYPE like '%' || #{map.like_trainingType} || '%'
  
  
    and omt.TRAINING_TYPE in (#{i})
  
  
    and omt.ADAPT_OBJECT = #{map.adaptObject}
  
  
    and omt.ADAPT_OBJECT like '%' || #{map.like_adaptObject} || '%'
  
  
    and omt.ADAPT_OBJECT in (#{i})
  
  
    and omt.REGISTRATION_TIME >= #{map.begin_registrationTime}
  
  
    and omt.REGISTRATION_TIME <= #{map.end_registrationTime}
  
  
    and omt.REGISTRATION_DEADLINE >= #{map.begin_registrationDeadline}
  
  
    and omt.REGISTRATION_DEADLINE <= #{map.end_registrationDeadline}
  
  
    and omt.TRAINING_START_TIME >= #{map.begin_trainingStartTime}
  
  
    and omt.TRAINING_START_TIME <= #{map.end_trainingStartTime}
  
  
    and omt.TRAINING_END_TIME >= #{map.begin_trainingEndTime}
  
  
    and omt.TRAINING_END_TIME <= #{map.end_trainingEndTime}
  
  
    and omt.ADDRESS = #{map.address}
  
  
    and omt.ADDRESS like '%' || #{map.like_address} || '%'
  
  
    and omt.ADDRESS in (#{i})
  
  
    and omt.REGISTRATION_WAY = #{map.registrationWay}
  
  
    and omt.REGISTRATION_WAY like '%' || #{map.like_registrationWay} || '%'
  
  
    and omt.REGISTRATION_WAY in (#{i})
  
  
    and omt.MAX_PLAYERS = #{map.maxPlayers}
  
  
    and omt.MAX_PLAYERS in (#{i})
  
  
    and omt.TRAINING_WAY = #{map.trainingWay}
  
  
    and omt.TRAINING_WAY like '%' || #{map.like_trainingWay} || '%'
  
  
    and omt.TRAINING_WAY in (#{i})
  
  
    and omt.PRICE = #{map.price}
  
  
    and omt.PRICE in (#{i})
  
  
    and omt.FILE_ID = #{map.fileId}
  
  
    and omt.FILE_ID like '%' || #{map.like_fileId} || '%'
  
  
    and omt.FILE_ID in (#{i})
  
  
    and omt.FILE_NAME = #{map.fileName}
  
  
    and omt.FILE_NAME like '%' || #{map.like_fileName} || '%'
  
  
    and omt.FILE_NAME in (#{i})
  
  
    and omt.PREVIEW_FILE_ID = #{map.previewFileId}
  
  
    and omt.PREVIEW_FILE_ID like '%' || #{map.like_previewFileId} || '%'
  
  
    and omt.PREVIEW_FILE_ID in (#{i})
  
  
    and omt.PREVIEW_FILE_NAME = #{map.previewFileName}
  
  
    and omt.PREVIEW_FILE_NAME like '%' || #{map.like_previewFileName} || '%'
  
  
    and omt.PREVIEW_FILE_NAME in (#{i})
  
  
    and omt.BRIEF = #{map.brief}
  
  
    and omt.BRIEF like '%' || #{map.like_brief} || '%'
  
  
    and omt.BRIEF in (#{i})
  
  
    and omt.BRIEF_FILE_ID = #{map.briefFileId}
  
  
    and omt.BRIEF_FILE_ID like '%' || #{map.like_briefFileId} || '%'
  
  
    and omt.BRIEF_FILE_ID in (#{i})
  
  
    and omt.BRIEF_FILE_NAME = #{map.briefFileName}
  
  
    and omt.BRIEF_FILE_NAME like '%' || #{map.like_briefFileName} || '%'
  
  
    and omt.BRIEF_FILE_NAME in (#{i})
  
  
    and omt.ANNOUNCEMENTS = #{map.announcements}
  
  
    and omt.ANNOUNCEMENTS like '%' || #{map.like_announcements} || '%'
  
  
    and omt.ANNOUNCEMENTS in (#{i})
  
  
    and omt.ANNO_FILE_ID = #{map.annoFileId}
  
  
    and omt.ANNO_FILE_ID like '%' || #{map.like_annoFileId} || '%'
  
  
    and omt.ANNO_FILE_ID in (#{i})
  
  
    and omt.ANNO_FILE_NAME = #{map.annoFileName}
  
  
    and omt.ANNO_FILE_NAME like '%' || #{map.like_annoFileName} || '%'
  
  
    and omt.ANNO_FILE_NAME in (#{i})
  
  
    and omt.CONTACT = #{map.contact}
  
  
    and omt.CONTACT like '%' || #{map.like_contact} || '%'
  
  
    and omt.CONTACT in (#{i})
  
  
    and omt.PHONE = #{map.phone}
  
  
    and omt.PHONE like '%' || #{map.like_phone} || '%'
  
  
    and omt.PHONE in (#{i})
  
  
    and omt.MOBILE = #{map.mobile}
  
  
    and omt.MOBILE like '%' || #{map.like_mobile} || '%'
  
  
    and omt.MOBILE in (#{i})
  
  
    and omt.EMAIL = #{map.email}
  
  
    and omt.EMAIL like '%' || #{map.like_email} || '%'
  
  
    and omt.EMAIL in (#{i})
  
  
    and omt.SORT_INDEX = #{map.sortIndex}
  
  
    and omt.SORT_INDEX in (#{i})
  
  
    and omt.IS_VISIBLE = #{map.isVisible}
  
  
    and omt.IS_VISIBLE in (#{i})
  
  
    and omt.CREATE_TIME >= #{map.begin_createTime}
  
  
    and omt.CREATE_TIME <= #{map.end_createTime}
  
  
    and omt.UPDATE_TIME >= #{map.begin_updateTime}
  
  
    and omt.UPDATE_TIME <= #{map.end_updateTime}
